document.addEventListener('DOMContentLoaded', function(){ UI.init(); Router.init(); }); // router.js - simple SPA router with fade const Router = (function(){ const routes = { home: Templates.home, projetos: Templates.projetos, cadastro: Templates.cadastro }; function navigateTo(route){ const view = document.getElementById('app'); if(!view) return; view.classList.remove('fade-in'); view.classList.add('fade-out'); setTimeout(()=>{ view.innerHTML = routes[route](); view.classList.remove('fade-out'); view.classList.add('fade-in'); initScripts(route); }, 260); } function initScripts(route){ if(route==='cadastro'){ Validation.attachForm(document.getElementById('form-cadastro')); } document.querySelectorAll('[data-open-modal]').forEach(b=>b.addEventListener('click', ()=>UI.openModal(b.getAttribute('data-open-modal')))); } function handleLinks(e){ const link = e.target.closest('[data-link]'); if(link){ e.preventDefault(); const target = link.getAttribute('data-link'); navigateTo(target); document.querySelectorAll('.nav-list').forEach(n=>n.classList.remove('open')); } } function init(){ document.body.addEventListener('click', handleLinks); navigateTo('home'); } return { init, navigateTo }; })();// templates.js - page templates const Templates = (function(){ function home(){ return `<section class="hero grid grid-12 fade"><div class="col-7"><h1 class="hero-title">Organização Exemplo — Tecnologia e Impact</h1><p class="lead">Projetos que unem tecnologia e transformação social.</p><div class="actions"><a class="btn primary" href="#" data-link="projetos">Projetos</a><a class="btn ghost" href="#" data-link="cadastro">Cadastro</a></div></div><div class="col-5"><figure><img src="img/projetos/projeto-exemplo.webp" alt="Voluntários em ação"><figcaption>Voluntários em ação.</figcaption></figure></div></section>`; } function projetos(){ return `<section class="section fade"><h1>Projetos</h1><p>Descrição dos projetos e como participar.</p></section>`; } function cadastro(){ return `<section class="section fade" aria-labelledby="cad-title"><h1 id="cad-title">Formulário de Cadastro</h1><form id="form-cadastro" class="form" novalidate><fieldset><legend>Dados pessoais</legend><label for="nome">Nome completo</label><input id="nome" name="nome" required type="text"><label for="email">E-mail</label><input id="email" name="email" required type="email"><label for="cpf">CPF</label><input id="cpf" name="cpf" required type="text"><label for="telefone">Telefone</label><input id="telefone" name="telefone" required type="tel"><label for="nascimento">Data de nascimento</label><input id="nascimento" name="nascimento" required type="date"></fieldset><fieldset><legend>Endereço</legend><label for="endereco">Endereço</label><input id="endereco" name="endereco" required type="text"><label for="cep">CEP</label><input id="cep" name="cep" required type="text"><label for="cidade">Cidade</label><input id="cidade" name="cidade" required type="text"><label for="estado">Estado</label><select id="estado" name="estado" required><option value="">Selecione</option><option value="SP">SP</option><option value="RJ">RJ</option></select></fieldset><button type="submit" class="btn primary">Enviar cadastro</button></form></section>`; } return { home, projetos, cadastro }; })();